+// Open-Closed Principle
+
+#define PI  3.14159265359
+
+#define SHAPE_TYPE_CIRCLE       1
+#define SHAPE_TYPE_RECTANGLE    2
+
+struct Circle
+{
+    int x;
+    int y;
+    int radius;
+};
+
+struct Rectangle
+{
+    int x1;
+    int y1;
+    int x2;
+    int y2;
+};
+
+double calcArea(void *shape, int type)
+{
+    double tmp = 0;
+
+    if (type == SHAPE_TYPE_CIRCLE) {
+        Circle *pCircle = (Circle*)shape;
+        tmp = PI * std::pow(pCircle->radius, 2);
+    } 
+    else if (type == SHAPE_TYPE_RECTANGLE) {
+        Rectangle *pRectangle = (Rectangle*)shape;
+        tmp = (pRectangle->x2 - pRectangle->x1) * (pRectangle->y2 - pRectangle->y1);
+    }
+
+    return tmp; 
+}

